import React from 'react'
import { useEffect,useState } from 'react';

function Loading() {
    const [progress, setProgress] = useState(0);
    const [reached100,setReached100]=useState(0)
    useEffect(() => {
        // Create an interval to update the progress every 10ms
        const interval = setInterval(() => {
          if (!reached100) {
            setProgress((prevProgress) => {
              if (prevProgress >= 100) {
                setReached100(true); // Start decreasing once 100% is reached
                return 100;
              }
              return prevProgress + 2; // Increment by 2
            });
          } else {
            setProgress((prevProgress) => {
              if (prevProgress <= 0) {
                setReached100(false); // Start increasing once 0% is reached
                return 0;
              }
              return prevProgress - 2; // Decrement by 2
            });
          }
        }, 10); // Interval of 10ms
    
        // Cleanup the interval on component unmount
        return () => clearInterval(interval);
      }, [reached100]);

    return (
        <div className="w-full flex justify-center h-full items-center bg-black bg-opacity-70 fixed left-0 top-0 z-50">
        <div className="w-60  h-2 rounded-md overflow-hidden">
            <div
            className="bg-red-600 h-full progress-bar "
            style={{ width: `${progress}%` ,transition:' width 0.5s ease'}}
            ></div>
        </div>
        </div>
    );
    }

export default Loading
