import logo from './logo.svg';
import './App.css';
import Tempwelcomepage from './components/Tempwelcomepage';
import Signup from './pages/Signup';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Footer from './components/Footer';
import Loading from './components/Loading';
function App() {
  const [isLoading, setIsLoading] = useState(false);

  // Function to toggle the loading state
  const toggleLoading = () => {
    setIsLoading(!isLoading);
  };

  return (
    <Router>
      <div className="App">
      <button onClick={toggleLoading}>
        {isLoading ? 'Hide Loading' : 'Show Loading'}
      </button>

      {/* Conditionally render the Loading component based on isLoading */}
      {isLoading && <Loading />}
        <Tempwelcomepage/>
        <Loading/>
        {/* <Routes>
          <Route path="/" element={<Tempwelcomepage />} />
          <Route path='signup' element={<Signup/>}/>
        </Routes>    */}
        {/* <Footer/> */}
      </div>
    </Router>


  );
}

export default App;
